        <div class="info-container">
            <div class="info-card">
                <div class="card-title">
                    <i class="fas fa-info-circle"></i>
                    <h2>Informations Climatiques</h2>
                </div>
                <div id="climat_actuel">
                    <p>Cliquez sur une région pour voir les détails climatiques.</p>
                </div>
            </div>
            
            <div class="info-card">
                <div class="card-title">
                    <i class="fas fa-chart-bar"></i>
                    <h2>Statistiques Globales</h2>
                </div>
                <div class="stats-grid">
                    <div class="stat-box">
                        <div class="stat-value" id="total-regions">13</div>
                        <div class="stat-label">Régions</div>
                    </div>
                    <div class="stat-box">
                        <div class="stat-value" id="avg-temperature">-</div>
                        <div class="stat-label">Temp. moyenne</div>
                    </div>
                    <div class="stat-box">
                        <div class="stat-value" id="max-temperature">-</div>
                        <div class="stat-label">Temp. max</div>
                    </div>
                    <div class="stat-box">
                        <div class="stat-value" id="min-temperature">-</div>
                        <div class="stat-label">Temp. min</div>
                    </div>
                </div>
            </div>
            
            <div class="info-card">
                <div class="card-title">
                    <i class="fas fa-map-marked-alt"></i>
                    <h2>Légende et Contrôles</h2>
                </div>
                <p>Survolez une région pour voir son nom.</p>
                <p>Cliquez sur une région pour la sélectionner et afficher ses détails climatiques.</p>
                
            </div>
        </div>
    </div>
    
    <footer>
        <p>Carte interactive de France - Données climatiques</p>
    </footer>

    <div class="tooltip" id="map-tooltip"></div>

    <script>
       
        fetch("data/climat_actuel.json")
          .then(response => {
              if (!response.ok) throw new Error("Erreur de chargement du fichier JSON");
              return response.json();
          })
          .then(climatData => {

              
              const dataByRegion = {};
              climatData.forEach(d => {
                  dataByRegion[d.region] = d;
              });

              
              function getColor(value, min, max) {
                  const ratio = (value - min) / (max - min);
                  const red = Math.floor(255 * ratio);
                  const blue = 255 - red;
                  return `rgb(${red}, 0, ${blue})`;
              }

              // === Détermination des bornes min/max sur t_moy ===
              const tValues = climatData.map(d => d.t_moy);
              const tMin = Math.min(...tValues);
              const tMax = Math.max(...tValues);

              // === Mise à jour des statistiques globales ===
              document.getElementById('avg-temperature').textContent = (tValues.reduce((a, b) => a + b, 0) / tValues.length).toFixed(1) + '°C';
              document.getElementById('max-temperature').textContent = tMax + '°C';
              document.getElementById('min-temperature').textContent = tMin + '°C';

              // === Sélection du SVG et coloration ===
              const paths = document.querySelectorAll('g.region');
              const tooltip = document.getElementById('map-tooltip');
              
              paths.forEach(path => {
                  const regionName = path.getAttribute('data-nom');
                  const data = dataByRegion[regionName];
                  
                  if (data) {
                      path.style.fill = getColor(data.t_moy, tMin, tMax);
                  } else {
                      path.style.fill = '#ccc';
                  }

                  // Gestion du tooltip
                  path.addEventListener('mouseover', function(e) {
                      const name = this.getAttribute('data-nom');
                      tooltip.textContent = name;
                      tooltip.style.opacity = '1';
                      tooltip.style.left = (e.pageX + 10) + 'px';
                      tooltip.style.top = (e.pageY - 20) + 'px';
                  });
                  
                  path.addEventListener('mousemove', function(e) {
                      tooltip.style.left = (e.pageX + 10) + 'px';
                      tooltip.style.top = (e.pageY - 20) + 'px';
                  });
                  
                  path.addEventListener('mouseout', function() {
                      tooltip.style.opacity = '0';
                  });
              });

              // === Interaction clic pour afficher les infos ===
              const infoDiv = document.getElementById('climat_actuel');
              let selectedRegion = null;
              
              paths.forEach(path => {
                  path.addEventListener('click', () => {
                      // Désélectionner la région précédente
                      if (selectedRegion) {
                          selectedRegion.classList.remove('selected');
                      }
                      
                      // Sélectionner la nouvelle région
                      path.classList.add('selected');
                      selectedRegion = path;
                      
                      const regionName = path.getAttribute('data-nom');
                      const data = dataByRegion[regionName];
                      
                      if (data) {
                          infoDiv.innerHTML = `
                              <div class="card-title">
                                  <i class="fas fa-map-marker-alt"></i>
                                  <h2>${data.region}</h2>
                              </div>
                              <div class="data-item">
                                  <span>Date :</span>
                                  <span class="data-value">${data.date}</span>
                              </div>
                              <div class="data-item">
                                  <span>Température moyenne :</span>
                                  <span class="data-value">${data.t_moy} °C</span>
                              </div>
                              <div class="data-item">
                                  <span>Température max :</span>
                                  <span class="data-value">${data.t_max} °C</span>
                              </div>
                              <div class="data-item">
                                  <span>Température min :</span>
                                  <span class="data-value">${data.t_min} °C</span>
                              </div>
                              <div class="data-item">
                                  <span>Précipitations :</span>
                                  <span class="data-value">${data.precipitations} mm</span>
                              </div>
                              <div class="data-item">
                                  <span>Anomalie :</span>
                                  <span class="data-value">${data.anomalie} °C</span>
                              </div>
                          `;
                      } else {
                          infoDiv.innerHTML = `
                              <div class="card-title">
                                  <i class="fas fa-exclamation-triangle"></i>
                                  <h2>${regionName}</h2>
                              </div>
                              <p>Aucune donnée disponible pour cette région.</p>
                          `;
                      }
                  });
              });
          })
          .catch(error => {
              console.error("Erreur :", error);
              document.getElementById("climat_actuel").innerHTML = `
                  <div class="card-title">
                      <i class="fas fa-exclamation-triangle"></i>
                      <h2>Erreur</h2>
                  </div>
                  <p>Impossible de charger les données climatiques.</p>
              `;
          });
    </script>
